import logo from './logo.svg';
import './style.css'
import Button from './Button';
import  React, {Component}  from 'react';
import { toHaveStyle } from '@testing-library/jest-dom/dist/matchers';



export default class Theme extends Component {
  state ={
    text:'Dark',
    color:'',
    txt:"Theme"
  }
  Change1 = () => {

    this.setState({
      txt: "Dark"
    })


 }
 Change2 = () => {

  this.setState({
    txt: "Light"
  })


}
Change3 = () => {

  this.setState({
    txt: "Party"
  })


}

  render() {

    if(this.state.text==="Dark"){

      <div className={this.state.txt} id='Theme' style={{backgroundColor:"white"}}>
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        {/* <Button color={this.state.text}/> */}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    if(this.state.text==="Light"){
      <div className="Theme" id='Theme' style={{backgroundColor:"blue"}}>
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        {/* <Button color={this.state.text}/> */}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    if(this.state.text==="Party"){
      <div className="Theme" id='Theme' style={{backgroundColor:"black"}}>
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        {/* <Button color={this.state.text}/> */}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    return (
      <>
      <div className="Theme" id='Theme' >
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>
      </>
    )
  }
  }
