import logo from './logo.svg';
import './style.css'
import Button from './Button';
import  React, {Component}  from 'react';
import { toHaveStyle } from '@testing-library/jest-dom/dist/matchers';



export default class Theme extends Component {
  state ={
    text:'',
    color:''
  }
  Change1 = (event) => {
    let text = event.target.innerText;
    this.setState({
      text: "Dark"
    })


 }
 Change2 = (event) => {
  let text = event.target.innerText;
  this.setState({
    text: "Light"
  })


}
Change3 = (event) => {
  let text = event.target.innerText;
  this.setState({
    text: "Party"
  })


}

  render() {
    if(this.state.text==="Dark"){
      <div className="Theme" id='Theme' style={{backgroundColor:""}}>
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        {/* <Button color={this.state.text}/> */}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    if(this.state.text==="Dark"){
      <div className="Theme" id='Theme' style={{backgroundColor:"blue"}}>
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        {/* <Button color={this.state.text}/> */}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    if(this.state.text==="Dark"){
      <div className="Theme" id='Theme' style={{backgroundColor:"black"}}>
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        {/* <Button color={this.state.text}/> */}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    return (
      <>
      <div className="Theme" id='Theme' >
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
        {/* <Button color={this.state.text}/> */}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>
      </>
    )
  }
  }
