import logo from './logo.svg';
import './style.css'
import Button from './Button';
import  React, {Component}  from 'react';
import { toHaveStyle } from '@testing-library/jest-dom/dist/matchers';



export default class Theme extends Component {
  state ={
    text:'Dark',
    color:'',
    txt:"White"
  }

  Change1 = () => {

    this.setState({
      txt: "Dark"
    })


 }
 Change2 = () => {

  this.setState({
    txt: "Light"
  })


}
Change3 = () => {

  this.setState({
    txt: "Party"
  })


}

  render() {

    var result=""

    if(this.state.txt==="White"){

     result = <div className="Theme" id='Theme' >
        <h1>React Themes Demo</h1>
        <div className='btn'>

        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    if(this.state.txt==="Dark"){

     result = <div className="Theme" id='Theme' style={{backgroundColor:"black"}} >
        <h1>React Themes Demo</h1>
        <div className='btn'>

        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>

    }
    if(this.state.txt==="Light"){
      <div className="Theme" id='Theme' style={{backgroundColor:"lightblue"}}>
      <h1>React Themes Demo</h1>
      <div className='btn'>

      </div>
      <img src={logo} className="App-logo" alt="logo" />
    </div>

    }
    if(this.state.txt==="Party"){
      <div className="Theme" id='Theme' style={{backgroundImage:"linear-gradient(to right, cyan, green)"}}>
      <h1>React Themes Demo</h1>
      <div className='btn'>

      </div>
      <img src={logo} className="App-logo" alt="logo" />
    </div>

    }

    return ( 
      <>
      <button onClick={this.Change1}>Dark</button>
        <button  onClick={this.Change2}>Light</button>
        <button  onClick={this.Change3}>Party</button>
      {result}
      </>
    )
  }
  }
