import logo from './logo.svg';
import './style.css'
import Button from './Button';
import  React, {Component}  from 'react';



export default class Theme extends Component {

  Change = (event) => {
    let id = event.target.id;
    if(id == 'Dark') {
     document.getElementById('Theme').style.backgroundColor = 'black'
    }
    else if(id == 'Light') {
      document.getElementById('Theme').style.backgroundColor = 'lightblue'
     }
     else {
      while(1) {
      document.getElementById('Theme').style.backgroundColor = 'lr'
      }
     }
 }

  render() {
    return (
      <div className="Theme" id='Theme'>
        <h1>React Themes Demo</h1>
        <div className='btn'>
        <Button color={'Dark'} change={this.Change }/>
        <Button color={'Light'} change={this.Change }/>
        <Button color={'Party'} change={ this.Change }/>
        </div>
        <img src={logo} className="App-logo" alt="logo" />
      </div>
    )
  }
  }
